################################################################################
#
#  Orbit stabilizer
#
################################################################################

function _orbit_stabilizer(G, idity, a)
  OT = Tuple{typeof(idity), FakeFmpqMat}[(idity, _hnf_integral(basis_matrix(FakeFmpqMat, a)))]
  Y = typeof(idity)[]
  m = 1
  while m <= length(OT)
    b = OT[m][2]
    for g in G
      c = _operate(g, b)
      i = findfirst(x -> x[2] == c, OT)
      if i isa Nothing
        push!(OT, (OT[m][1] * g, c))
      elseif i isa Int
        w = OT[m][1] * g * inv(OT[i][1])
        if !(w in Y || inv(w) in Y)
          push!(Y, w)
        end
      end
    end
    m += 1
  end

  return OT, Y
end

function _operate(g::AbstractAssociativeAlgebraElem, b)
  M = representation_matrix(g, :right)
  c = _hnf_integral(b * FakeFmpqMat(M))
  return c
end
