@testset "Misc" begin
   Qx, x = FlintQQ["x"];
   K, _a = NumberField(x^8 + 80004*x^6 + 2400240008*x^4 + 32004799999992*x^2 + 160032003200160004, "_a")
  b = *_a^7 - *_a^6 + *_a^5 - *_a^4 + *_a^3 - *_a^2 + *_a - 
  t, c = is_power(b, 8)
  @test t == true
  @test c^8 == b
end

@testset "rand" begin
  Qx,  x = PolynomialRing(FlintQQ, "x");
  K,  a = NumberField(x,"a");
  O = maximal_order(K)
  m0 = O(9)*O
  Q = NfOrdQuoRing(O, m0)

  for f in (rand(Q), rand(rng, Q))
    @test f isa elem_type(Q)
  end
  @test rand(Q, 2) isa Vector{elem_type(Q)}

  Random.seed!(rng, rand_seed)
  x = rand(rng, Q)
  Random.seed!(rng, rand_seed)
  @test x == rand(rng, Q)
end
